(* shared *)
let sum_and_print l = (List.fold_left (+) 0 l) |> print_int; print_newline ();;

(* part 1 *)
data
|> List.map (fun x ->
        (function X -> 1 | Y -> 2 | Z -> 3) (snd x)
        +
        match x with
        | (A,Y) | (B,Z) | (C,X) -> 6
        | (A,Z) | (B,X) | (C,Y) -> 0
        | _                     -> 3)
|> sum_and_print
;;

(* part 2 *)
data
|> List.map (
        let lose = function
                | A -> 3
                | B -> 1
                | C -> 2
        in
        let succ = function
                | A -> B
                | B -> C
                | C -> A
        in
        let draw x = lose (succ x) in
        let win  x = lose (succ (succ x)) in
        (function
                | a,X -> lose a + 0
                | a,Y -> draw a + 3
                | a,Z -> win  a + 6))
|> sum_and_print
;;
